mapper.update:
UPDATE MonthlyBalance
 SET ActBalance = ActBalance + #{tranAmount}
WHERE ActId = #{actId}
 AND Yr=#{tranYear}
 AND Mth=#{tranMonth}

mapper.select:

SELECT
   ActId as actId,
   Yr as yr,
   Mth as mth,
   ActBalance as actBalance
FROM MonthlyBalance
 WHERE ActId = #{actId}
 AND Yr =#{tranYear}
 AND Mth=#{tranMonth};



         SELECT
           SUM(mb.ActBalance) as actBalance
        FROM MonthlyBalance mb
		  INNER JOIN ACCOUNTS act
	  		ON mb.actid = act.actid
         WHERE act.ACTNAME = #{actName}
         AND mb.Yr =#{tranYear}
         AND mb.Mth=#{tranMonth};


SELECT
   SUM(ActBalance) as actBalance
FROM MonthlyBalance
 WHERE ActId = #{actId}
 AND Yr = #{tranYear}
 AND Mth <= #{tranMonth};

mapper.update:
  	UPDATE BudgetDetail 
  	 SET Amount=#{amount}
	 WHERE BudgetId=#{budgetId}
	  AND ActId=#{actId}
	  AND Mth=#{mth}

UPDATE BudgetDetail 
 SET Amount= #{amount}
  WHERE BudgetId=#{budgetId}
   AND ActId = #{actId}

mapper.delete:
	    DELETE FROM BudgetDetail
    	  WHERE ActId = #{actId}
    	  	AND BudgetId = #{budgetId}
