mapper.update:
UPDATE PLANEVENTS
SET 
	EVENTTYPEID = #{eventTypeId},
	EVENTVALUE = #{eventValue},
	EVENTVALUETYPE = #{eventValueType},
	STARTAGE = #{startAge},
	ENDAGE = #{endAge}
WHERE PLANID =  #{planID}
	AND EVENTNAME = #{eventName}

mapper.delete:
	    DELETE FROM PlanEvents
    	  WHERE PlanID = #{planID}
    	  	AND EventName = #{eventName}
